import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from './api/axios'
const CATALOG_URL = '/v1/catalog';

const CatalogList = (props) => {
    const [catalog, setCatalog] = useState([])
    const [cartData={userid:'',item:[]}, setCartData] = useState()
    const [itemArray,setItemArray]=useState([])
    const navigate = useNavigate()
    useEffect(() => {
        catList()
    }, [])

    const catList = async () => {
        // const response = await axios.get(CATALOG_URL)
        const response = [{
            "id": 1,
            "itemName": "T-Shirt", "description": "T-Shirt For Mens", "amount": 100, "imageUrl": ""
        },{
            "id": 2,
            "itemName": "Reebok Shoes", "description": "Reebok Shoes For Men", "amount": 200, "imageUrl": ""
        },{
            "id": 3,
            "itemName": "Nike Shoes", "description": "Nike Shoes For Men", "amount": 250, "imageUrl": ""
        },{
            "id": 4,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        },{
            "id": 5,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        }]
        // const response = []
        setCatalog(response)

        console.log('cat', catalog);
    }

    const insertToCatalog=()=>{
        navigate('/catalog-add')
    }
    const navigateToCart=()=>{
        navigate('/kart')
    }

    const addToCart = (id,quantity) => {
        console.log('id', id,quantity);
        const arr=[]
        let data = {
            itemId:id,
            quantity:quantity
        }
        arr.push(data)
        setItemArray([...itemArray,data])
        console.log('arr',itemArray);
        // setCartData({...cartData,userid:1,item:arr})
        // console.log('cart',cartData);
    }
    return (
        <div className='container-fluid cat'>
            <div className="row">
                <div className="col-md-12 d-flex justify-content-end">
                <a onClick={()=>insertToCatalog()} className='btn btn-outline-primary me-2'>Add Catalog Item</a>
                <a onClick={()=>navigateToCart()} className='btn btn-outline-primary'>Cart</a>
                </div>
            </div>
            {catalog.length !==0?<div className="row d-flex justify-content align-item flex-wrap mt-2">
            {
                    catalog.map(element=>{
                        return(<div className="col-md-3 col" key={element.id}>
                        <div className="card" style={{ width: "15rem" }}>
                            <img src={element?.imageUrl} className="img" alt="" />
                            <div className="card-body">
                                <h5 className="card-title">â‚¹{element?.amount}</h5>
                                <p className="card-text">{element?.itemName}</p>
                                <p className="card-text">{element?.description}</p>
                                {itemArray.length !=0?itemArray.map(item=>{
                                    console.log('id',item.itemId,element.id);
                                    item.itemId != element.id?<a onClick={()=>addToCart(element?.id,1)} className="btn btn-outline-primary">Add To Cart</a>:<>
                                        <a  className="btn btn-outline-danger">-</a>
                                        <a  className="btn btn-outline-primary">+</a>
                                    </>
                                }):<a onClick={()=>addToCart(element?.id,1)} className="btn btn-outline-primary">Add To Cart</a>}

                            </div>
                        </div>
                    </div>)
                    })
                }
            </div>:<></>}

        </div>
    )
}

export default CatalogList
