import React, { useState } from 'react'
import { useLocation } from 'react-router-dom';

const Payment = (props) => {
  const {state} = useLocation()
  console.log('state',state);
  const [payment={cardHolder:'',cardNumber:'',monthYear:'',cvc:''},setPayment]=useState();
  const onPayment=async()=>{
    try {
      console.log('payment',payment);
    } catch (error) {
      console.log('paymentError',error);
    }
  }
  return (
    <div className='container'>
      <div className="row">
        <div className="col-md-12 d-flex justify-content-center align-item-center">
          <div className="card">
            <div className="body">
              <form className='p-2'>
                <div className="form-group">
                  <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Card holder*</label>
                    <input type="text" value={payment?.cardHolder} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" required  onChange={(cardHolder)=>setPayment({...payment,cardHolder:cardHolder})}/>
                  </div>
                </div>
                <div className="form-group">
                  <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Credt/debt card number*</label>
                    <input type="text" value={payment?.cardNumber} className="form-control" id="exampleInputPassword1" required onChange={(cardNumber)=>setPayment({...payment,cardNumber:cardNumber})}/>
                  </div>
                </div>
                <div className="form-group row">
                  <div className="mb-3 col">
                  <label className="form-check-label" htmlFor="expire">Expriration month and year*</label>
                    <input type="text" value={payment?.monthYear} className="form-control" id="expire" maxLength={7} required onChange={(monthYear)=>setPayment({...payment,monthYear:monthYear})}/>
                  </div>
                  <div className="mb-3 col">
                  <label className="form-check-label" htmlFor="cvc">CVC*</label>
                    <input type="text" value={payment?.cvc} className="form-control" id="cvc" maxLength={3} required onChange={(cvc)=>setPayment({...payment,cvc:cvc})}/>
                  </div>
                </div>
                <button type="submit" className="btn btn-outline-primary" onSubmit={()=>onPayment()}>Submit</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Payment
