import React, { useState, useEffect } from 'react'

const Kart = () => {
  const [cart, setCart] = useState()
  let total = 0
  let quantity = 0
  useEffect(() => {
    getCart()
  }, [])

  const getCart = () => {
    // const response = await axios.get(CATALOG_URL)
    const response = {
      "id": 1,
      "userId": 1,
      "item": [
        {
          "itemId": "Reebook Shoes",
          "quantity": 2,
          "cost": 200,
          "price": 200
        },
        {
          "itemId": "T-Shirt For Mens",
          "quantity": 1,
          "cost": 200,
          "price": 200
        }
      ]
    }
    setCart(response)
  }

  const navigateToCatalog=()=>{
    navigate('/catalog')
}

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className="row">
          <div className="col-md-12 d-flex justify-content-end">
            <a onClick={() => navigateToCatalog()} className='btn btn-outline-primary'>Back</a>
          </div>
        </div>
        <div className="col-md-12 p-2">
          <table className='table'>
            <thead>
              <tr>
                {/* <th>Product</th> */}
                <th>Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                {/* <th></th> */}
              </tr>
            </thead>
            <tbody>
              {
                cart?.item.map((element) => {
                  total += element?.price
                  quantity += element?.quantity
                  return (<tr>
                    {/* <td><img src={} alt="" /></td> */}
                    <td>{element?.itemId}</td>
                    <td>{element?.cost}</td>
                    <td>{element?.quantity}</td>
                    <td>{element?.price}</td>
                    {/* <td><i className="material-icons">&#xe872;</i></td> */}
                  </tr>)
                })
              }

            </tbody>
          </table>
          <h3 id="cart-total">Cart Total</h3>
          <div className='d-flex justify-content-between'>
            <span>Cart Totals</span>
            <span>Number of items: {quantity}</span>
            <span>Total: {total}</span>
          </div>
          <div className='d-flex justify-content-end pt-5'>
            <button className='btn btn-outline-primary'>Checkout</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Kart
