import React, { useState } from 'react'
import { useLocation, useNavigate } from 'react-router-dom';

const Payment = (props) => {
  const location = useLocation()
  const navigate = useNavigate()
  console.log('state', location.state);
  const [payment = { cardHolder: '', cardNumber: '', monthYear: '', cvc: '' }, setPayment] = useState();
  const [upi,setUPI] =useState()
  const onPayment = async (event) => {
    try {
      event.preventDefault()
      console.log('payment', payment);
      // navigate('/order',{state:{paymentid:1}})
      navigate('/success')
    } catch (error) {
      console.log('paymentError', error);
    }
  }
  const onUpiPayment = ()=>{
    try {
      console.log('upi',upi);
    } catch (error) {
      console.log('upiError',error);
    }
  }
  const byMode = (mode) => {
    if (mode === 'CARD') {
      return <form className='p-2' onSubmit={onPayment}>
        <div className="form-group">
          <div className="mb-3">
            <label htmlFor="exampleInputEmail1" className="form-label">Card holder*</label>
            <input type="text" value={payment?.cardHolder} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" required onChange={(ch) => setPayment({ ...payment, cardHolder: ch.target.value })} />
          </div>
        </div>
        <div className="form-group">
          <div className="mb-3">
            <label htmlFor="exampleInputPassword1" className="form-label">Credt/debt card number*</label>
            <input type="text" value={payment?.cardNumber} className="form-control" id="exampleInputPassword1" required onChange={(cn) => setPayment({ ...payment, cardNumber: cn.target.value })} />
          </div>
        </div>
        <div className="form-group row">
          <div className="mb-3 col">
            <label className="form-check-label" htmlFor="expire">Expriration month and year*</label>
            <input type="text" value={payment?.monthYear} className="form-control" id="expire" maxLength={5} required onChange={(my) => setPayment({ ...payment, monthYear: my.target.value })} />
          </div>
          <div className="mb-3 col">
            <label className="form-check-label" htmlFor="cvc">CVC*</label>
            <input type="text" value={payment?.cvc} className="form-control" id="cvc" maxLength={3} required onChange={(cv) => setPayment({ ...payment, cvc: cv.target.value })} />
          </div>
        </div>
        <button type="submit" className="btn btn-outline-primary">Submit</button>
      </form>
    } else if (mode === 'UPI') {
      return <>
      <input type='text' value={upi} onChange={(text)=>setUPI(text.target.value)}/>
      <button onClick={()=>onUpiPayment()}>Submit</button>
      </>
    }else if (mode === 'COD') {
      return
    }
  }
  return (
    <div className='container'>
      <div className="row">
        <div className="col-md-12 d-flex justify-content-center align-item-center">
          <div className="card">
            <div className="body">

            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Payment
