import React, { useState, useEffect } from 'react'
import axios from './api/axios'
import Header from './Header';
const CATALOG_URL = '/v1/catalog';

const CatalogList = (props) => {
    const [catalog, setCatalog] = useState([])
    const [cartData = { userid: '', item: [] }, setCartData] = useState()
    const [itemArray, setItemArray] = useState([])
    useEffect(() => {
        catList()
    }, [])

    const catList = async () => {
        // const response = await axios.get(CATALOG_URL)
        const response = [{
            "id": 1,
            "itemName": "T-Shirt", "description": "T-Shirt For Mens", "amount": 100, "imageUrl": "https://edwiki-webserver-config.s3.amazonaws.com/1"
        }, {
            "id": 2,
            "itemName": "Reebok Shoes", "description": "Reebok Shoes For Men", "amount": 200, "imageUrl": ""
        }, {
            "id": 3,
            "itemName": "Nike Shoes", "description": "Nike Shoes For Men", "amount": 250, "imageUrl": ""
        }, {
            "id": 4,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        }, {
            "id": 5,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        }]
        // const response = []
        setCatalog(response)
    }

    const addToCart = (element) => {
        let data = {
            itemId: element?.id,
            quantity: element?.quantity
        }
        setItemArray([...itemArray, data]);
        toggleProductQuantity(element)
        if (itemArray.length) {
            console.log('ItemArray', itemArray);
            setCartData({userid:1,item:itemArray})
        }
        console.log('cartData', cartData);

    }
    const incre =(element)=>{
        if (itemArray.length) {
            itemArray?.forEach((v)=>{
                console.log('elev',v);
                if(v?.itemId===element?.id){
                    v.quantity +=1
                }
                console.log('element',itemArray);
                // setCartData({userid:1,item:itemArray})
            });
        }
    }
    const decre =(element)=>{
        if (itemArray.length) {
            itemArray?.forEach((v)=>{
                console.log('elem',v);
                if(v?.itemId===element?.id){
                    if ( v.quantity >=1) {
                        v.quantity =v.quantity-1
                    } else if(v.quantity ==0){
                        let newArray = itemArray.filter(v=>v.quantity!=0)
                        setItemArray([...itemArray,newArray])
                    }
                    console.log('v',itemArray);
                }
            });
        }
    }

    const toggleProductQuantity =(element)=>{
        console.log('array',itemArray);
        console.log('element',element);
        let inde
        if (itemArray.length) {
            itemArray?.forEach((v)=>{
                console.log('isMatch',v?.itemId,element?.id)
                if (v?.itemId==element?.id) {
                    console.log('here');
                   return <>
                            <a className="btn btn-outline-danger me-2" onClick={()=>decre(element)}>-</a>
                            <a className="btn btn-outline-primary" onClick={()=>incre(element)}>+</a>
                        </>
                } else {
                    return <a onClick={() => addToCart(element, 1)} className="btn btn-outline-primary">Add To Cart</a>
                }
            });
        }else{
            return <a onClick={() => addToCart(element, 1)} className="btn btn-outline-primary">Add To Cart</a>
        }

    }
    return (
        <div className='container-fluid cat'>
            <div className="row">
                <Header/>
            </div>
            {catalog.length !== 0 ? <div className="row d-flex justify-content align-item flex-wrap mt-2">
                {
                    catalog.map(element => {
                        return (<div className="col-md-3 col mb-2" key={element.id}>
                            <div className="card" style={{ width: "15rem" }}>
                                <img src={element?.imageUrl} className="img-fluid card-image" alt="" />
                                <div className="card-body">
                                    <h5 className="card-title">â‚¹{element?.amount}</h5>
                                    <p className="card-text">{element?.itemName}</p>
                                    <p className="card-text">{element?.description}</p>
                                    {toggleProductQuantity(element)}
                                </div>
                            </div>
                        </div>)
                    })
                }
            </div> : <></>}

        </div>
    )
}

export default CatalogList
