import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from './api/axios'

const Kart = (props) => {
  const [cart, setCart] = useState()
  const navigate = useNavigate()
  let total = 0
  let quantity = 0
  useEffect(() => {
    getCart()
  }, [])

  const getCart = () => {
