    }
    setCart(response)
  }

  const navigateToCatalog=()=>{
    navigate('/catalog')
  }

  const onCheckout=(id,price)=>{
    if (id && price) {
      navigate('/payment',{state:{id:id,price:price}})
    }
  }

  return (
    <div className='container-fluid vh-100 p-2'>
      <div className="row">
          <div className="col-md-12 d-flex justify-content-start">
            <a onClick={() => navigateToCatalog()} className='btn btn-outline-primary'>Back</a>
          </div>
        </div>
      <div className='row'>
        <div className="col-md-12 p-2">
          <table className='table'>
            <thead>
              <tr>
                {/* <th>Product</th> */}
                <th>Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                {/* <th></th> */}
              </tr>
            </thead>
            <tbody>
              {cart?
                cart?.item.map((element) => {
                  total += element?.price
                  quantity += element?.quantity
                  return (<tr>
                    {/* <td><img src={} alt="" /></td> */}
                    <td>{element?.itemId}</td>
                    <td>{element?.cost}</td>
                    <td>{element?.quantity}</td>
                    <td>{element?.price}</td>
                    {/* <td><i className="material-icons">&#xe872;</i></td> */}
                  </tr>)
                }):(<tr>
                  <td></td>
                    <td></td>
                    <td></td>
                    <td></td>
                </tr>)
              }

            </tbody>
          </table>
          <h3 id="cart-total">Cart Total</h3>
          <div className='d-flex justify-content-between'>
            <span>Cart Totals</span>
            <span>Number of items: {quantity?quantity:0}</span>
            <span>Total: {total?total:0}</span>
          </div>
          <div className='d-flex justify-content-end pt-5'>
            <button className='btn btn-outline-primary' onClick={()=>onCheckout(cart?.id,total)}>Checkout</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Kart
