import React,{useState,useEffect} from 'react'
import axios from './api/axios'
const CATALOG_URL = '/v1/catalog';

const CatalogList = () => {
    const [catalog,setCatalog]=useState()
    useEffect(() => {
      catList()
    }, [])

    const catList=async()=>{
        const response = await axios.get(CATALOG_URL)
        setCatalog(response)
    }
    return (
        <div className='container-fluid border cat'>
            <div className="row">
                {
                    catalog.forEach(element => {
                        <div className="col-md-2 col">
                    <div className="card" style={{width: "5rem;"}}>
                        <img src={element?.imageUrl} className="img" alt=""/>
                            <div className="card-body">
                                <h5 className="card-title">{element?.amount}</h5>
                                <p className="card-text">{element?.itemName}</p>
                                <p className="card-text">{element?.description}</p>
                                <a href="#" className="btn btn-outline-primary">Add To Cart</a>
                            </div>
                    </div>
                </div>
                    })
                }

            </div>
        </div>
    )
}

export default CatalogList
