import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from './api/axios'
const CATALOG_URL = '/v1/catalog';

const CatalogList = (props) => {
    const [catalog, setCatalog] = useState([])
    const [cartData = { userid: '', item: [] }, setCartData] = useState()
    const [itemArray, setItemArray] = useState([])
    const navigate = useNavigate()
    useEffect(() => {
        catList()
    }, [])

    const catList = async () => {
        // const response = await axios.get(CATALOG_URL)
        const response = [{
            "id": 1,
            "itemName": "T-Shirt", "description": "T-Shirt For Mens", "amount": 100, "imageUrl": "https://edwiki-webserver-config.s3.amazonaws.com/1"
        }, {
            "id": 2,
            "itemName": "Reebok Shoes", "description": "Reebok Shoes For Men", "amount": 200, "imageUrl": ""
        }, {
            "id": 3,
            "itemName": "Nike Shoes", "description": "Nike Shoes For Men", "amount": 250, "imageUrl": ""
        }, {
            "id": 4,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        }, {
            "id": 5,
            "itemName": "Shirt", "description": "Shirt For Mens", "amount": 400, "imageUrl": ""
        }]
        // const response = []
        setCatalog(response)

        console.log('cat', catalog);
    }

    const insertToCatalog = () => {
        navigate('/catalog-add')
    }
    const navigateToCart = () => {
        navigate('/kart')
    }

    const addToCart = (id, quantity) => {
        console.log('id', id, quantity);
        const arr = []
        let data = {
            itemId: id,
            quantity: quantity
        }
        arr.push(data)
        setItemArray([...itemArray, data])
        console.log('arr', itemArray);
        // setCartData({...cartData,userid:1,item:arr})
        // console.log('cart',cartData);
    }
    return (
        <div className='container-fluid cat'>
            <div className="row">
                <nav class="navbar navbar-expand-lg">
                    <div class="container-fluid">
                        <a class="navbar-brand" href="#">Navbar</a>
                        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span class="navbar-toggler-icon"></span>
                        </button>
                        <div class="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul class="navbar-nav ms-auto mb-2 mb-lg-0">
                                <li class="nav-item">
                                    <a class="nav-link active" aria-current="page" onClick={() => insertToCatalog()}>Add Catalog Item</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" onClick={() => navigateToCart()}>Cart</a>
                                </li>
                                <li class="nav-item dropdown">
                                    <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                        Account
                                    </a>
                                    <ul class="dropdown-menu">
                                        <li><a class="dropdown-item" href="#">Profile</a></li>
                                        <li><a class="dropdown-item" href="#">Logout</a></li>
                                    </ul>
                                </li>
                            </ul>
                        </div>
                    </div>
                </nav>
                {/* <div className="col-md-12 d-flex justify-content-end">
                    <a onClick={() => insertToCatalog()} className='btn btn-outline-primary me-2'>Add Catalog Item</a>
                    <a onClick={() => navigateToCart()} className='btn btn-outline-primary me-2'>Cart</a>
                    <a onClick={() => navigateToCart()} className='btn btn-outline-primary'>Cart</a>
                </div> */}
            </div>
            {catalog.length !== 0 ? <div className="row d-flex justify-content align-item flex-wrap mt-2">
                {
                    catalog.map(element => {
                        return (<div className="col-md-3 col mb-2" key={element.id}>
                            <div className="card" style={{ width: "15rem" }}>
                                <img src={element?.imageUrl} className="img-fluid card" alt="" />
                                <div className="card-body">
                                    <h5 className="card-title">â‚¹{element?.amount}</h5>
                                    <p className="card-text">{element?.itemName}</p>
                                    <p className="card-text">{element?.description}</p>
                                    {itemArray.length != 0 ? itemArray.map(item => {
                                        console.log('id', item?.itemId, element?.id);
                                        (item && item?.itemId === element?.id) ?
                                            <>
                                                <a className="btn btn-outline-danger">-</a>
                                                <a className="btn btn-outline-primary">+</a>
                                            </> : <a onClick={() => addToCart(element?.id, 1)} className="btn btn-outline-primary">Add To Cart</a>
                                    }) : <a onClick={() => addToCart(element?.id, 1)} className="btn btn-outline-primary">Add To Cart</a>}

                                </div>
                            </div>
                        </div>)
                    })
                }
            </div> : <></>}

        </div>
    )
}

export default CatalogList
